

import ted
import sys
import getopt

def main(argv) :

    

    #print("len : %d" % len(argv)) 
    cmd=""
    for i in range(1, len(argv)) :
        #print("argv[%d] : [%s]"% (i, argv[i]))
        if i!=1 :
            cmd+=' '
        cmd+=argv[i]
    
    ted.PatternConnect()
    
    if ted.PatternIsConnect() :
        #print("Cmd is [%s]"%cmd);
        ted.PatternSetCommand(cmd)

        ted.PatternDisconnect()


if __name__ == "__main__" :
    main(sys.argv)

