from TModule.Device import *
from TModule import Aging
import inspect

from Aging.SC import SC_0000


class Job(Aging.JobBase) :

    def __init__(this, device): 
        Aging.JobBase.__init__(this, inspect.getfile(inspect.currentframe()), device, "JOB_0000")

        #TC 등록
        this.Add(SC_0000.SC(device))  # SC0 1번 수행 


if __name__ == "__main__" :
    
    device = TDevice(TDevice.Type.TESys)
    print("Connect To " + device.GetName() )
    isConnect = device.Connect()
    assert isConnect 

    job = Job(device)
    job.Run()

    #Disconnect
    print("Disconnect from " + device.GetName() )
    device.Disonnect()
    print("End of Exam. Bye!!")
    print()
    
    
    

