from TModule.Device import *
from TModule import Aging
import inspect

from Aging.TC import TC_0001 

class SC(Aging.SCBase) :

    def __init__(this, device): 
        Aging.SCBase.__init__(this, inspect.getfile(inspect.currentframe()), device, "SC_0000")

        #TC 등록
        this.Add(TC_0001.TC(device))  # TC0 1번 수행 
    

if __name__ == "__main__" :

    device = TDevice(TDevice.Type.TESys)
    print("Connect To " + device.GetName() )
    isConnect = device.Connect()
    assert isConnect 

    sc = SC(device)
    sc.Run()

    #Disconnect
    print("Disconnect from " + device.GetName() )
    device.Disonnect()
    print("End of Exam. Bye!!")
    print()

    

 


